let currentQuestionIndex = 0;
let totalCoins = 0;
let studentName = "";
let studentRoll = "";
let studentClass = "";
let clickCount = 0; 
let coinAwarded = false; 
let quizQuestions = []; 

function shuffleArray(array) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        const temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}


// ########## Screen Control Functions ##########

function updateCoinDisplay() {
    document.querySelector('#current-score span').textContent = totalCoins;
}

function showStudyMaterial() {
    studentName = document.getElementById('student-name').value.trim();
    studentRoll = document.getElementById('student-roll').value.trim();
    studentClass = document.getElementById('student-class').value.trim();

    if (studentName === "" || studentRoll === "" || studentClass === "") {
        alert("‡§ï‡•É‡§™‡§Ø‡§æ ‡§Ö‡§™‡§®‡§æ ‡§®‡§æ‡§Æ, ‡§∞‡•ã‡§≤ ‡§®‡§Ç‡§¨‡§∞ ‡§î‡§∞ ‡§ï‡§ï‡•ç‡§∑‡§æ ‡§≠‡§∞‡•á‡§Ç‡•§");
        return;
    }
    
    // --- Prepare Study Material Content ---
    const studyListDiv = document.getElementById('study-material-list');
    studyListDiv.innerHTML = '';
    
    // 1. Add 20 Short Answer Questions
    shortAnswerQuestions.forEach((item, index) => {
        const itemDiv = document.createElement('div');
        itemDiv.classList.add('qa-item');
        
        const questionHtml = `<div class="qa-question">**‡§™‡•ç‡§∞‡§∂‡•ç‡§® (‡§µ‡§ø‡§∏‡•ç‡§§‡•É‡§§) ${index + 1}:** ${item.q}</div>`;
        const cleanAnswer = item.a.trim(); 
        const answerHtml = `<div class="qa-answer">**‡§â‡§§‡•ç‡§§‡§∞:** ${cleanAnswer.replace(/\n/g, '<br>')}</div>`;
        
        itemDiv.innerHTML = questionHtml + answerHtml;
        studyListDiv.appendChild(itemDiv);
    });

    // 2. Add 30 Objective Questions
    const hr = document.createElement('hr');
    studyListDiv.appendChild(hr);
    
    const objHeading = document.createElement('h3');
    objHeading.style.color = '#ff6347';
    objHeading.textContent = '‡§µ‡§∏‡•ç‡§§‡•Å‡§®‡§ø‡§∑‡•ç‡§† ‡§™‡•ç‡§∞‡§∂‡•ç‡§®‡•ã‡§§‡•ç‡§§‡§∞ (‡§ï‡•ç‡§µ‡§ø‡§ú ‡§Ö‡§≠‡•ç‡§Ø‡§æ‡§∏ ‡§ï‡•á ‡§≤‡§ø‡§è)';
    studyListDiv.appendChild(objHeading);
    

    objectiveQuestions.forEach((q, index) => {
        const itemDiv = document.createElement('div');
        itemDiv.classList.add('qa-item');
        
        const correctOptionIndex = q.answer;
        const correctOptionText = q.options[correctOptionIndex];
        
        const questionHtml = `<div class="qa-question">**‡§™‡•ç‡§∞‡§∂‡•ç‡§® (‡§µ‡§∏‡•ç‡§§‡•Å‡§®‡§ø‡§∑‡•ç‡§†) ${index + 1}:** ${q.word}</div>`;
        const answerHtml = `<div class="qa-answer">**‡§∏‡§π‡•Ä ‡§â‡§§‡•ç‡§§‡§∞:** ${correctOptionText}</div>`;
        
        itemDiv.innerHTML = questionHtml + answerHtml;
        studyListDiv.appendChild(itemDiv);
    });

    document.getElementById('info-screen').style.display = 'none';
    document.getElementById('study-screen').style.display = 'block';
}


function startQuiz() {
    // Select 25 random questions from the pool of 30
    const pool = [...objectiveQuestions]; 
    shuffleArray(pool); 
    quizQuestions = pool.slice(0, 25); // Take the first 25 

    currentQuestionIndex = 0; 
    totalCoins = 0; 

    document.getElementById('quiz-screen').querySelector('h2').textContent = `üìù ‡§µ‡§ø‡§ú‡§Ø‡§®‡§ó‡§∞ ‡§∏‡§æ‡§Æ‡•ç‡§∞‡§æ‡§ú‡•ç‡§Ø ‡§™‡§∞ ‡§Ü‡§ß‡§æ‡§∞‡§ø‡§§ ${quizQuestions.length} ‡§µ‡§∏‡•ç‡§§‡•Å‡§®‡§ø‡§∑‡•ç‡§† ‡§™‡•ç‡§∞‡§∂‡•ç‡§®`;


    document.getElementById('study-screen').style.display = 'none';
    document.getElementById('quiz-screen').style.display = 'block';
    updateCoinDisplay();
    loadNextQuestion();
}

function finishQuiz() {
    document.getElementById('quiz-screen').style.display = 'none';
    document.getElementById('result-screen').style.display = 'block';
    generateMarkSheet();
}

function handleNextQuestion() {
    currentQuestionIndex++;
    loadNextQuestion();
}

// ########## Quiz Logic ##########

function loadNextQuestion() {
    if (currentQuestionIndex >= quizQuestions.length) {
        finishQuiz();
        return;
    }

    clickCount = 0;
    coinAwarded = false; 
    document.getElementById('next-button').style.display = 'none';
    const quizArea = document.getElementById('quiz-area');
    quizArea.innerHTML = ''; 
    
    const feedbackDiv = document.getElementById('quiz-screen').querySelector('#feedback-message');
    if (feedbackDiv) feedbackDiv.innerHTML = '';

    const qData = quizQuestions[currentQuestionIndex];
    
    const html = `
        <div class="question-box">
            <h3>‡§™‡•ç‡§∞‡§∂‡•ç‡§® ${currentQuestionIndex + 1}: ${qData.word}</h3>
            <div id="feedback-message" style="margin-bottom: 10px;"></div>
            ${qData.options.map((option, index) => `
                <div class="option" data-index="${index}" onclick="handleOptionClick(this, ${index})">${String.fromCharCode(65 + index)}. ${option}</div>
            `).join('')}
        </div>
    `;
    quizArea.innerHTML = html;
}

function handleOptionClick(element, optionIndex) {
    if (document.getElementById('next-button').style.display !== 'none') {
        return; 
    }

    const qData = quizQuestions[currentQuestionIndex];
    const isCorrect = (optionIndex === qData.answer);
    const feedbackElement = document.getElementById('feedback-message');
    
    clickCount++;

    if (isCorrect) {
        element.classList.add('correct');
        
        if (clickCount === 1) {
            totalCoins += 2;
            coinAwarded = true;
            feedbackElement.innerHTML = `<span style="color: #28a745; font-weight: bold;">**‡§â‡§§‡•ç‡§ï‡•É‡§∑‡•ç‡§ü!** ‡§Ø‡§π ‡§Ü‡§™‡§ï‡§æ ‡§™‡§π‡§≤‡§æ ‡§∏‡§π‡•Ä ‡§™‡•ç‡§∞‡§Ø‡§æ‡§∏ ‡§π‡•à‡•§ ‡§Ü‡§™‡§ï‡•ã 2 ‡§Ö‡§Ç‡§ï ‡§Æ‡§ø‡§≤‡•á! üåü</span>`;
        } else if (clickCount > 1 && !coinAwarded) {
            feedbackElement.innerHTML = `<span style="color: #28a745;">‡§Ø‡§π ‡§∏‡§π‡•Ä ‡§â‡§§‡•ç‡§§‡§∞ ‡§π‡•à, ‡§≤‡•á‡§ï‡§ø‡§® ‡§Ü‡§™‡§ï‡•ã ‡§Ö‡§Ç‡§ï ‡§®‡§π‡•Ä‡§Ç ‡§Æ‡§ø‡§≤‡•á ‡§ï‡•ç‡§Ø‡•ã‡§Ç‡§ï‡§ø ‡§Ø‡§π ‡§Ü‡§™‡§ï‡§æ ‡§™‡§π‡§≤‡§æ ‡§ï‡•ç‡§≤‡§ø‡§ï ‡§®‡§π‡•Ä‡§Ç ‡§•‡§æ‡•§</span>`;
        }
        
        document.querySelectorAll('.option').forEach(opt => opt.onclick = null);
        document.getElementById('next-button').style.display = 'block';
        updateCoinDisplay();

    } else {
        element.classList.add('incorrect');
        
        if (clickCount === 1) {
            coinAwarded = false; 
            feedbackElement.innerHTML = `<span style="color: #dc3545; font-weight: bold;">**‡§ó‡§≤‡§§!** ‡§á‡§∏ ‡§™‡•ç‡§∞‡§∂‡•ç‡§® ‡§ï‡•á ‡§≤‡§ø‡§è ‡§ï‡•ã‡§à ‡§Ö‡§Ç‡§ï ‡§®‡§π‡•Ä‡§Ç ‡§Æ‡§ø‡§≤‡•á‡§ó‡§æ‡•§ ‚ùå</span>`;
            
            document.querySelectorAll('.option').forEach(opt => opt.onclick = null);
            document.querySelector(`.option[data-index="${qData.answer}"]`).onclick = function() {
                handleOptionClick(this, qData.answer);
            };
        } else {
            feedbackElement.innerHTML = `<span style="color: #dc3545;">‡§Ö‡§≠‡•Ä ‡§≠‡•Ä ‡§ó‡§≤‡§§ ‡§π‡•à‡•§ ‡§Ö‡§¨ ‡§ï‡•ã‡§à ‡§Ö‡§Ç‡§ï ‡§®‡§π‡•Ä‡§Ç ‡§Æ‡§ø‡§≤‡•á‡§ó‡§æ‡•§ ‚ùå</span>`;
            element.onclick = null; 
        }
    }
}

// ########## Generate Mark Sheet ##########

function generateMarkSheet() {
    const totalQuestions = quizQuestions.length; 
    const maxCoins = totalQuestions * 2; 
    
    const percentage = (totalCoins / maxCoins) * 100;
    
    let grade;
    if (percentage >= 90) grade = "A+ (‡§Ö‡§§‡§ø ‡§â‡§§‡•ç‡§ï‡•É‡§∑‡•ç‡§ü)";
    else if (percentage >= 80) grade = "A (‡§â‡§§‡•ç‡§ï‡•É‡§∑‡•ç‡§ü)";
    else if (percentage >= 70) grade = "B+ (‡§¨‡§π‡•Å‡§§ ‡§Ö‡§ö‡•ç‡§õ‡§æ)";
    else if (percentage >= 60) grade = "B (‡§Ö‡§ö‡•ç‡§õ‡§æ)";
    else grade = "C (‡§∏‡•Å‡§ß‡§æ‡§∞ ‡§ï‡•Ä ‡§Ü‡§µ‡§∂‡•ç‡§Ø‡§ï‡§§‡§æ)";

    const markSheetDiv = document.getElementById('mark-sheet');
    markSheetDiv.innerHTML = `
        <p><strong>‡§∏‡•ç‡§ï‡•Ç‡§≤ ‡§ï‡§æ ‡§®‡§æ‡§Æ:</strong> ${SCHOOL_NAME}</p>
        <p><strong>‡§µ‡§ø‡§¶‡•ç‡§Ø‡§æ‡§∞‡•ç‡§•‡•Ä ‡§ï‡§æ ‡§®‡§æ‡§Æ:</strong> ${studentName}</p>
        <p><strong>‡§∞‡•ã‡§≤ ‡§®‡§Ç‡§¨‡§∞:</strong> ${studentRoll}</p>
        <p><strong>‡§ï‡§ï‡•ç‡§∑‡§æ:</strong> ${studentClass}</p>
        <hr>
        <p><strong>‡§ï‡•Å‡§≤ ‡§™‡•ç‡§∞‡§∂‡•ç‡§® (‡§µ‡§∏‡•ç‡§§‡•Å‡§®‡§ø‡§∑‡•ç‡§†):</strong> ${totalQuestions}</p>
        <p><strong>‡§Ö‡§ß‡§ø‡§ï‡§§‡§Æ ‡§Ö‡§Ç‡§ï:</strong> ${maxCoins}</p>
        <p><strong>‡§™‡•ç‡§∞‡§æ‡§™‡•ç‡§§ ‡§ï‡•Å‡§≤ ‡§Ö‡§Ç‡§ï:</strong> <strong style="font-size: 24px;">${totalCoins}</strong></p>
        <p><strong>‡§™‡•ç‡§∞‡§§‡§ø‡§∂‡§§ (‡§Ö‡§Ç‡§ï‡•ã‡§Ç ‡§ï‡•á ‡§Ü‡§ß‡§æ‡§∞ ‡§™‡§∞):</strong> <strong>${percentage.toFixed(2)}%</strong></p>
        <p><strong>‡§ó‡•ç‡§∞‡•á‡§°:</strong> <strong style="font-size: 28px; color: ${percentage >= 80 ? '#28a745' : '#ff6347'};">${grade}</strong></p>
    `;
}